import React from "react";
import tw from "twin.macro";
import AnimationRevealPage from "helpers/AnimationRevealPage.js";
import Hero from "components/hero/BackgroundAsImageWithCenteredContent.js";
import SliderCard from "components/cards/ThreeColSlider.js";
import Testimonial from "components/testimonials/ThreeColumnWithProfileImage.js";
import DownloadApp from "components/cta/DownloadApp.js";
import MainFeature from "components/features/TwoColWithButton.js";
import Footer from "components/footers/SimpleFiveColumn.js";
import logo from "images/InnovativeColorsLogo.png";
import { Container } from "components/misc/Layouts.js";
import WebsiteTabs from "components/hero/WebSiteTabs.js";
import { SectionHeading } from "components/misc/Headings";
import TwoColumnWithImage from "components/testimonials/TwoColumnWithImage";
import TabGrid from "components/cards/TabCardGrid.js";

const Subheading = tw.span`tracking-wider text-sm font-medium`;
const Heading = tw(SectionHeading)``;
const HighlightedText = tw.span`bg-primary-500 text-gray-100 px-4 transform -skew-x-12 inline-block`;
const HighlightedTextInverse = tw.span`bg-gray-100 text-primary-500 px-4 transform -skew-x-12 inline-block`;
const Description = tw.span`inline-block mt-8`;
const imageCss = tw`rounded-4xl`;

var bgColors = { "default": "#001d28",
                    "bodyColor": "#121212",
                    "headingColor":'#ffffff'
};

export default () => (
  <Container>
    <img class="innovativeColorsLogo" src={logo} height={50} width={150} alt="Logo"/>
    <WebsiteTabs></WebsiteTabs>
  <AnimationRevealPage>


    <div style={{
        backgroundColor: bgColors.bodyColor,
        color:bgColors.headingColor
      }}>

    <Heading>ABOUT US</Heading>

    </div>

    <div style={{
        backgroundColor: bgColors.bodyColor,
        color:'white'
      }}>

      <div>
        <TwoColumnWithImage>

        </TwoColumnWithImage>
      </div>

      <SliderCard />

      <div style={{
        color:bgColors.headingColor
      }}>
    <Heading>WORKS</Heading>
    </div>

      <TabGrid
        heading={
          <>
            Checkout our <HighlightedText>menu.</HighlightedText>
          </>
        }
      />

    <Testimonial
        subheading=""
        heading={<>OUR TEAM</>}
      />

    </div>
      {/* <DownloadApp
        text={<>Learn with Augmented Reality technology. Download the app<HighlightedTextInverse>Yathi AR.</HighlightedTextInverse></>}
      /> */}
    <Footer />
  </AnimationRevealPage>
  </Container>
);
