import React from "react";
import tw from "twin.macro";
import AnimationRevealPage from "helpers/AnimationRevealPage.js";
import Hero from "components/hero/BackgroundAsImageWithCenteredContent.js";
import SliderCard from "components/cards/ThreeColSlider.js";
import Testimonial from "components/testimonials/ThreeColumnWithProfileImage.js";
import DownloadApp from "components/cta/DownloadApp.js";
import MainFeature from "components/features/TwoColWithButton.js";
import Footer from "components/footers/SimpleFiveColumn.js";
import logo from "images/InnovativeColorsLogo.png";
import { Container } from "components/misc/Layouts.js";
import WebsiteTabs from "components/hero/WebSiteTabs.js"

const Subheading = tw.span`tracking-wider text-sm font-medium`;
const HighlightedText = tw.span`bg-primary-500 text-gray-100 px-4 transform -skew-x-12 inline-block`;
const HighlightedTextInverse = tw.span`bg-gray-100 text-primary-500 px-4 transform -skew-x-12 inline-block`;
const Description = tw.span`inline-block mt-8`;
const imageCss = tw`rounded-4xl`;


export default () => (
  <Container>
    <img class="innovativeColorsLogo" src={logo} height={100} width={200} alt="Logo"/>
  <AnimationRevealPage>
  <WebsiteTabs></WebsiteTabs>
    <SliderCard />
          <Heading>Our Works</Heading>
          <Controls>
            <PrevButton onClick={sliderRef?.slickPrev}><ChevronLeftIcon/></PrevButton>
            <NextButton onClick={sliderRef?.slickNext}><ChevronRightIcon/></NextButton>
          </Controls>
    <MainFeature
        subheading={<Subheading>Established Since 2014</Subheading>}
        heading={
          <>
            We've been serving for
            <wbr /> <HighlightedText>over 5 years.</HighlightedText>
          </>
        }
        description={
          <Description>
            Innovative Colors studio is a core design driven company which is a one-stop solution based in Bangalore. Our company designs and develops a wide array of animations and interactive media. Our team of talented and well experienced animators  pushes the boundaries of visual and technical production at right direction.
            <br />
            <br />
            Innovative Colors stands tall in its domain and crew with strong creative background with global experience. We manifest creative talent which can be recognized by creative industry ethics. We are developing visually enticing and industry superior quality products with globally acknowledged reputation in Creativity, Skills and Quality experience. We are rapidly gaining popularity with our successful works and services.
          </Description>
        }
        buttonRounded={false}
        textOnLeft={false}
        primaryButtonText="Latest Offers"
        imageSrc={
          "https://images.unsplash.com/photo-1623346016470-53d81533a76d?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1974&q=80"
        }
        imageCss={imageCss}
        imageDecoratorBlob={true}
        imageDecoratorBlobCss={tw`left-1/2 -translate-x-1/2 md:w-32 md:h-32 opacity-25`}
      />
    <Testimonial
        subheading=""
        heading={<><HighlightedText>Founders</HighlightedText></>}
      />
      <DownloadApp
        text={<>Learn with Augmented Reality technology. Download the app<HighlightedTextInverse>Yathi AR.</HighlightedTextInverse></>}
      />
    <Footer />
  </AnimationRevealPage>
  </Container>
);
